mapper.select:
SELECT COUNT(*)
FROM action_operation ao


        AND ao.id = #{id}


        AND ao.user_id = #{userId}


        AND ao.news_id = #{newsId}


        AND ao.type = #{type}

SELECT id, user_id, news_id, type, times, create_time
FROM action_operation ao


        AND ao.id = #{id}


        AND ao.user_id = #{userId}


        AND ao.news_id = #{newsId}


        AND ao.type = #{type}


        AND ao.create_time BETWEEN #{startTime} AND #{endTime}



    LIMIT #{current},#{size}
